YUI.add("gallery-get-selection",function(a){a.getSelection=function(){var c,b,d;if(a.config.win.getSelection){b=a.config.win.getSelection();if(b.rangeCount>0){c=b.getRangeAt(0);}}else{if(a.config.doc.selection){c=a.config.doc.selection.createRange();}}if(c&&c.cloneContents){d=c.cloneContents();}else{if(c&&c.htmlText){d=a.Node.create(c.htmlText);}}return a.Node.create("<div></div>").append(d);};},"gallery-2012.10.17-20-00",{requires:["node"]});